import React from 'react';
import connect from '@vkontakte/vk-connect';

connect.send("VKWebAppInit", {});

const apiId = 7179993
const accessToken = "6d86abfdde5413a09385c136b06aef2716ac6915006e47221534e16cb672467bdcb2333b23ae40459b2fc"
const userId = 28047883
const URL = "https://oauth.vk.com/authorize?client_id=28047883&display=page&redirect_uri=https://oauth.vk.com/blank.html&scope=friends&response_type=token&v=5.52"


  class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        token: null
      };
    }

    componentDidMount() {
      fetch(URL).then(res => res.json()).then(json => {
        this.setState({ token: json });
      });
    }

    render() {
      if (!token) return <div>Loading</div>;
      return <div>{JSON.stringify(token)}</div>;
    }
  }

export default App;
