import React from 'react';
import connect from '@vkontakte/vk-connect';
import User from './component/user'
import InfoUser from './component/infoUser'

connect.send("VKWebAppInit", {});

const apiId = 7179993
const userId = 2128351
const serviceToken = "b37ecad3b37ecad3b37ecad3dfb313440abb37eb37ecad3eed06cec6fe72e9c4e2a1714"
const url = "https://api.vk.com/method/users.get?user_ids="+userId+"&fields=bdate&access_token="+serviceToken+"&v=5.102"
const proxyurl = "https://cors-anywhere.herokuapp.com/"


class App extends React.Component {
  state = {
    id: undefined,
    firstName: undefined,
    lastName: undefined,
    bdate: undefined
  }

  gettingUser = async (e) => {
    e.preventDefault()
    const apiUrl = await fetch(proxyurl+url)
    const data = await apiUrl.json()
    console.log(data.response[0])
    this.setState({
      id: data.response[0].id,
      firstName: data.response[0].first_name,
      lastName: data.response[0].last_name,
      bdate: data.response[0].bdate
    })
  }

  render () {
    return (
      <div>
        <User userMethod={this.gettingUser} />
        <InfoUser id={this.state.id} firstName={this.state.firstName} lastName={this.state.lastName} bdata={this.state.bdata}/>
      </div>
    )
  }
}

export default App;
