import React from 'react';
import connect from '@vkontakte/vk-connect';

connect.send("VKWebAppInit", {});

const apiId = 7179993
const accessToken = "6d86abfdde5413a09385c136b06aef2716ac6915006e47221534e16cb672467bdcb2333b23ae40459b2fc"
const userId = 28047883


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      token: null 
    };
  }

  componentDidMount() {
    fetch("https://oauth.vk.com/authorize?client_id=7179993&display=page&redirect_uri=https://oauth.vk.com/blank.html&scope=friends&response_type=token&v=5.52")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      return (
        <ul>
          {items.map(item => (
            <li key={item.name}>
              {item.name} {item.price}
            </li>
          ))}
        </ul>
      );
    }
  }
}

export default App;
